<?php if ( ! defined('andromed')) exit(''); //есть ли авторизация//необходимо изменить этот файл - сделать проверку cookies$global['authorization'] = false;$global['user'] = null;//точно нет авторизацииif ( $_COOKIE['hash'] == false || !isset ($_COOKIE['hash']) ) {	$global['authorization'] = false;}//может и естьelse {	$where = "NOT(`deleted`) AND `login`='{$_COOKIE['login']}' AND `id`='{$_COOKIE['id']}'";	$where .= " ORDER BY `id` DESC";	$what = "`id`, `login`, `hash`, `pass`";	$globaluser = $db->select_line ("{$config['prefix']}users", $where, '*', "authorization.php");		$hash = md5 ($globaluser['id'] . substr($globaluser['hash'], 2, 10) . substr($globaluser['pass'], 2, 16) . $globaluser['login']);		if (! $globaluser) {		//нет авторизации		$global['authorization'] = false;	}	else {		if ($_COOKIE['hash'] == $hash) {			//есть авторизация			$global['user'] = $globaluser;			$global['authorization'] = true;		}		else {			//нет авторизации			$global['authorization'] = false;		}	}}?>