<?php if ( ! defined('andromed')) exit(''); $global['authorization'] = false;$global['user'] = null;$cookie_id 		= $regular->num($_POST['cooid']);$cookie_login 	= $regular->eng_num_sol($_POST['coologin']);$cookie_hash 	= $regular->eng_num($_POST['coohash']);if (	$cookie_id !== false	&& $cookie_login !== false	&& $cookie_hash !== false){	$where = "NOT(`deleted`) AND `login`='{$cookie_login}' AND `id`='{$cookie_id}'";	$where .= " ORDER BY `id` DESC";	$what = "`id`, `login`, `hash`, `pass`, `status`";	$globaluser = $db->select_line ("{$config['prefix']}users", $where, $what, "authorization_java.php");		$hash = md5 ($globaluser['id'] . substr($globaluser['hash'], 2, 10) . substr($globaluser['pass'], 2, 16) . $globaluser['login']);	if (! $globaluser) {		$global['authorization'] = false;	}	else {		if ($cookie_hash == $hash) {			//есть авторизация			$global['user'] = $globaluser;			$global['authorization'] = true;		}		else {			$global['authorization'] = false;		}	}}?>